-- Model definitions
-- SYS_MODEL
IF (SELECT COUNT(name) FROM SYSOBJECTS WHERE ID = OBJECT_ID(N'SYS_MODEL') AND OBJECTPROPERTY(ID, 'IsTable') = 1) > 0 DROP TABLE SYS_MODEL;
IF (SELECT COUNT(name) FROM SYSOBJECTS WHERE ID = OBJECT_ID(N'SYS_MODEL') AND OBJECTPROPERTY(ID, 'IsTable') = 1) <= 0 CREATE TABLE SYS_MODEL( NAME VARCHAR(256) NOT NULL UNIQUE, ROOT_FOLDER VARCHAR(256) NOT NULL,FILE_DATA VARCHAR(256),FILE_SCHEMA VARCHAR(256) NOT NULL, FILE_MAPPING VARCHAR(256) NOT NULL, IN_USE BIT NOT NULL, OBJ_LEVEL INT NOT NULL, INIT_ORDER INT NOT NULL,INIT_SUB_ORDER INT NOT NULL, PRIMARY KEY(NAME));
-- OAU_CODE
IF (SELECT COUNT(name) FROM SYSOBJECTS WHERE ID = OBJECT_ID(N'OAU_CODE') AND OBJECTPROPERTY(ID, 'IsTable') = 1) > 0 DROP TABLE OAU_CODE;
IF (SELECT COUNT(name) FROM SYSOBJECTS WHERE ID = OBJECT_ID(N'OAU_CODE') AND OBJECTPROPERTY(ID, 'IsTable') = 1) <= 0 CREATE TABLE OAU_CODE( CODE VARCHAR(10) NOT NULL UNIQUE, AUTHENTICATION IMAGE );
